import React from 'react';
import './App.css';
import api from './api'
import PostView from './Components/PostView'

import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';

import Typography from '@material-ui/core/Typography';



class App extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      title : '',
      content : '',
      results : [],

    }

  }

  componentDidMount(){
      this.getPosts()
  }
  async getPosts(){
      const _results = await api.getAllPosts()
      //_results.data 현재 아무것도 없음
      this.setState({results : _results.data})
      console.log(_results)
  }






  handlingChange = (event) => {
    this.setState({[event.target.name] : event.target.value})
  }



  handlingSubmit = async (event) => {
    event.preventDefault() //evnet의 디폴트한 기능들을 막기
    let result = await api.createPost({title : this.state.title, content : this.state.content})
    console.log("completed",result)
    this.setState({title : '', content : ''})
    this.getPosts()


  }


  handlingDelete = async (event) => {
    await api.deletePost(event.target.value)
    this.getPosts()

  }


  render(){
  return (
    <div className="App">
      <Container maxWidth="sm">
      <div className="PostingSection">
        <Paper className="PostingPaper">
          <h2>대나무 숲 글 작성</h2>
          <form className="PostingForm" onSubmit={this.handlingSubmit}>

          <TextField
          id="standard-multiline-flexible"
          label="title"
          name="title"
          multiline
          rowsMax="4"
          value={this.state.title}
          onChange={this.handlingChange}
        />


          <TextField
          id="standard-multiline-flexible"
          label="content"
          name="content"
          multiline
          rowsMax="4"
          value={this.state.content}
          onChange={this.handlingChange}
          />




          <Button variant="contained" color="primary" type="submit">제출하기</Button>
          </form>
          </Paper>

      </div>
      <div className="ViewSection">
        {

          this.state.results.map((post) =>
          <Card className={'card'}>
      <CardContent>
        <Typography className={'card-title'} color="textSecondary" gutterBottom>

        </Typography>
        <Typography variant="h5" component="h2">
        <PostView key = {post.id} id = {post.id} title = {post.title} content = {post.content} />
        </Typography>
      </CardContent>
      <CardActions>
        <Button variant="contained" color="secondary" onClick={(event) => this.handlingDelete(post.id)} size="small">삭제하기</Button>
      </CardActions>
    </Card>
          )
        }


      </div>
      </Container>
    </div>

  );
}
}

export default App;
